const { Client } = require\(''pg''\);
const { v4: uuidv4 } = require\(''uuid''\);
const client = new Client\({ connectionString: process.env.DATABASE_URL, ssl: { rejectUnauthorized: false } }\);

async function createSchedulesForAllClinicians\(\) {
  await client.connect\(\);

  // Get all clinicians
  const clinicians = await client.query\(""SELECT id, \\""firstName\\"", \\""lastName\\"" FROM users WHERE ''CLINICIAN'' = ANY\(roles\) AND \\""isActive\\"" = true""\);
  console.log\(''Found'', clinicians.rows.length, ''clinicians in production''\);

  // Weekly schedule - available Mon-Fri 9am-5pm with lunch break
  const weeklySchedule = {
    monday: { isAvailable: true, startTime: ''09:00'', endTime: ''17:00'', breakStart: ''12:00'', breakEnd: ''13:00'' },
    tuesday: { isAvailable: true, startTime: ''09:00'', endTime: ''17:00'', breakStart: ''12:00'', breakEnd: ''13:00'' },
    wednesday: { isAvailable: true, startTime: ''09:00'', endTime: ''17:00'', breakStart: ''12:00'', breakEnd: ''13:00'' },
    thursday: { isAvailable: true, startTime: ''09:00'', endTime: ''17:00'', breakStart: ''12:00'', breakEnd: ''13:00'' },
    friday: { isAvailable: true, startTime: ''09:00'', endTime: ''17:00'', breakStart: ''12:00'', breakEnd: ''13:00'' },
    saturday: { isAvailable: false },
    sunday: { isAvailable: false }
  };

  for \(const clinician of clinicians.rows\) {
    // Check if schedule already exists
    const existing = await client.query\(''SELECT id FROM clinician_schedules WHERE \\""clinicianId\\"" = $1'', [clinician.id]\);

    if \(existing.rows.length > 0\) {
      console.log\(''Schedule already exists for'', clinician.firstName, clinician.lastName\);
      continue;
    }

    await client.query\(\\`
      INSERT INTO clinician_schedules 
      \(id, ""clinicianId"", ""weeklyScheduleJson"", ""acceptNewClients"", ""maxAppointmentsPerDay"", ""maxAppointmentsPerWeek"", ""bufferTimeBetweenAppointments"", ""availableLocations"", ""effectiveStartDate"", ""createdAt"", ""updatedAt"", ""createdBy"", ""lastModifiedBy""\)
      VALUES 
      \($1, $2, $3, true, 8, 40, 15, ARRAY[''IN_PERSON'', ''TELEHEALTH''], NOW\(\), NOW\(\), NOW\(\), $2, $2\)
    \\`, [uuidv4\(\), clinician.id, JSON.stringify\(weeklySchedule\)]\);

    console.log\(''Created schedule for'', clinician.firstName, clinician.lastName\);
  }

  await client.end\(\);
  console.log\(''\\nDone! All clinicians in production now have schedules.''\);
}

createSchedulesForAllClinicians\(\).catch\(e => { console.error\(e.message\); process.exit\(1\); }\);
")

